mapper.select:
		SELECT
		       A.ID
		  FROM TBL_MEMBER_ALIVE A
		 WHERE A.NAME = #{ name }
 		   AND A.ID_QUE_NUM = #{ idQueNum } 
		   AND A.ID_ANSWER = #{ idAnswer } 
		   AND A.USER_STATUS = 'A'

mapper.insert:
INSERT
  INTO TBL_MEMBER_DEAD A
(
  A.NO
, A.PHONE
, A.EMAIL
, A.ADDRESS
, A.ID
, A.PWD
, A.BIRTH
, A.NAME
, A.GENDER  
)  
VALUES
(
  CONCAT('D-', SEQ_MEMBER_DEAD_NO.NEXTVAL)	
, #{ phone }
, #{ email }
, #{ address }
, #{ id }
, #{ pwd }
, #{ birth }
, #{ name }  
, #{ gender }
)
